import React, { useState } from 'react';
import NavBar from '../components/NavBar';
import Footer from '../components/Footer';

function ScamChecker() {
  const [selectedPayment, setSelectedPayment] = useState(null);
  const [step, setStep] = useState(0); // 0: Payment selection, 1: Questions, 2: Result
  const [answers, setAnswers] = useState({
    who: '',
    urgency: false,
    secrecy: false,
    contactKnown: false, // For friend/family
  });
  const [result, setResult] = useState(null);

  const paymentTypes = [
    { type: 'Bank Transfer', description: 'Direct transfer to another bank account.' },
    { type: 'Credit/Debit Card', description: 'Payment via card details.' },
    { type: 'Cryptocurrency', description: 'Bitcoin, Ethereum, or other digital currency.' },
    { type: 'Gift Cards', description: 'Prepaid cards like Amazon or iTunes vouchers.' },
    { type: 'Cash', description: 'Physical cash or cheque payment.' },
  ];

  const handlePaymentSelect = (payment) => {
    setSelectedPayment(payment);
    setStep(1);
    setAnswers({ who: '', urgency: false, secrecy: false, contactKnown: false });
    setResult(null);
  };

  const handleAnswer = (key, value) => {
    setAnswers((prev) => ({ ...prev, [key]: value }));
  };

  const handleSubmit = () => {
    let riskLevel = '';
    let explanation = '';

    if (answers.who === 'friendOrFamily') {
      if (!answers.contactKnown && (selectedPayment.type === 'Bank Transfer' || selectedPayment.type === 'Cryptocurrency' || selectedPayment.type === 'Gift Cards')) {
        riskLevel = 'Extremely High Risk';
        explanation =
          'This may be an impersonation scam (e.g., "Hi Mum/Dad" scam). Never send money via ' +
          selectedPayment.type +
          ' unless you’ve confirmed the person’s identity using their usual contact method.';
      } else if (!answers.contactKnown && answers.urgency) {
        riskLevel = 'High Risk';
        explanation =
          'An urgent request via ' +
          selectedPayment.type +
          ' from an unknown contact claiming to be a loved one is suspicious. Verify their identity.';
      } else if (answers.contactKnown && answers.secrecy) {
        riskLevel = 'Moderate Risk';
        explanation =
          'Even if known, secrecy requests with ' + selectedPayment.type + ' are unusual. Confirm directly.';
      } else if (answers.contactKnown) {
        riskLevel = 'Low Risk';
        explanation = 'Verified contact via ' + selectedPayment.type + ' is likely safe, but confirm unusual requests.';
      } else {
        riskLevel = 'Moderate Risk';
        explanation = 'Verify identity before sending ' + selectedPayment.type + '.';
      }
    } else if (answers.who === 'business') {
      if (answers.urgency && answers.secrecy && (selectedPayment.type === 'Bank Transfer' || selectedPayment.type === 'Gift Cards')) {
        riskLevel = 'High Risk';
        explanation =
          'Urgent, secret requests via ' + selectedPayment.type + ' from a business are red flags. Verify officially.';
      } else if (selectedPayment.type === 'Cryptocurrency' || selectedPayment.type === 'Gift Cards') {
        riskLevel = 'Moderate Risk';
        explanation = selectedPayment.type + ' requests from businesses are uncommon. Confirm via official channels.';
      } else {
        riskLevel = 'Low Risk';
        explanation = 'No immediate red flags with ' + selectedPayment.type + ', but verify business requests.';
      }
    } else if (answers.who === 'unknown') {
      if (answers.urgency && (selectedPayment.type === 'Bank Transfer' || selectedPayment.type === 'Cryptocurrency' || selectedPayment.type === 'Gift Cards')) {
        riskLevel = 'High Risk';
        explanation = 'Urgent ' + selectedPayment.type + ' requests from unknowns are common scams. Do not proceed.';
      } else if (selectedPayment.type !== 'Credit/Debit Card') {
        riskLevel = 'Moderate Risk';
        explanation = selectedPayment.type + ' requests from unknowns are suspicious. Verify first.';
      } else {
        riskLevel = 'Low Risk';
        explanation = 'No immediate red flags with ' + selectedPayment.type + ', but stay cautious.';
      }
    } else {
      riskLevel = 'Low Risk';
      explanation = 'No specific risk identified for ' + selectedPayment.type + '. Stay vigilant.';
    }

    setResult({ riskLevel, explanation });
    setStep(2);
  };

  const reset = () => {
    setSelectedPayment(null);
    setStep(0);
    setResult(null);
  };

  return (
    <div className="min-h-screen bg-gray-900">
      <NavBar />
      <div className="pt-20 pb-10 max-w-3xl mx-auto px-4">
        <h1 className="text-3xl font-semibold text-gray-100 mt-8 mb-6">Scam Checker</h1>
        
        {step === 0 && (
          <div>
            <p className="text-base text-gray-300 mb-4">
              Select the type of payment someone is asking you to make to assess the risk.
            </p>
            <div className="space-y-4">
              {paymentTypes.map((payment) => (
                <button
                  key={payment.type}
                  onClick={() => handlePaymentSelect(payment)}
                  className="w-full text-left bg-gray-800 p-4 rounded-lg shadow-sm border border-gray-700 hover:bg-gray-700 focus:outline-none"
                >
                  <h2 className="text-xl font-semibold text-gray-300">{payment.type}</h2>
                  <p className="text-base text-gray-400">{payment.description}</p>
                </button>
              ))}
            </div>
          </div>
        )}

        {step === 1 && (
          <div>
            <p className="text-base text-gray-300 mb-4">
              Answer these questions about the {selectedPayment.type} request:
            </p>
            <div className="space-y-6">
              <div>
                <p className="text-base text-gray-300 mb-2">Who is requesting the payment?</p>
                <div className="space-x-4">
                  <button
                    onClick={() => handleAnswer('who', 'friendOrFamily')}
                    className={`p-2 rounded-lg border border-gray-700 ${answers.who === 'friendOrFamily' ? 'bg-blue-600 text-white' : 'bg-gray-800 text-gray-300'} hover:bg-gray-700`}
                  >
                    Friend or Family
                  </button>
                  <button
                    onClick={() => handleAnswer('who', 'business')}
                    className={`p-2 rounded-lg border border-gray-700 ${answers.who === 'business' ? 'bg-blue-600 text-white' : 'bg-gray-800 text-gray-300'} hover:bg-gray-700`}
                  >
                    Business
                  </button>
                  <button
                    onClick={() => handleAnswer('who', 'unknown')}
                    className={`p-2 rounded-lg border border-gray-700 ${answers.who === 'unknown' ? 'bg-blue-600 text-white' : 'bg-gray-800 text-gray-300'} hover:bg-gray-700`}
                  >
                    Unknown
                  </button>
                </div>
              </div>
              <div>
                <p className="text-base text-gray-300 mb-2">Is there urgency to send the payment?</p>
                <div className="space-x-4">
                  <button
                    onClick={() => handleAnswer('urgency', true)}
                    className={`p-2 rounded-lg border border-gray-700 ${answers.urgency ? 'bg-blue-600 text-white' : 'bg-gray-800 text-gray-300'} hover:bg-gray-700`}
                  >
                    Yes
                  </button>
                  <button
                    onClick={() => handleAnswer('urgency', false)}
                    className={`p-2 rounded-lg border border-gray-700 ${!answers.urgency ? 'bg-blue-600 text-white' : 'bg-gray-800 text-gray-300'} hover:bg-gray-700`}
                  >
                    No
                  </button>
                </div>
              </div>
              <div>
                <p className="text-base text-gray-300 mb-2">Are they asking you to keep it secret?</p>
                <div className="space-x-4">
                  <button
                    onClick={() => handleAnswer('secrecy', true)}
                    className={`p-2 rounded-lg border border-gray-700 ${answers.secrecy ? 'bg-blue-600 text-white' : 'bg-gray-800 text-gray-300'} hover:bg-gray-700`}
                  >
                    Yes
                  </button>
                  <button
                    onClick={() => handleAnswer('secrecy', false)}
                    className={`p-2 rounded-lg border border-gray-700 ${!answers.secrecy ? 'bg-blue-600 text-white' : 'bg-gray-800 text-gray-300'} hover:bg-gray-700`}
                  >
                    No
                  </button>
                </div>
              </div>
              {answers.who === 'friendOrFamily' && (
                <div>
                  <p className="text-base text-gray-300 mb-2">Is this their usual contact method?</p>
                  <div className="space-x-4">
                    <button
                      onClick={() => handleAnswer('contactKnown', true)}
                      className={`p-2 rounded-lg border border-gray-700 ${answers.contactKnown ? 'bg-blue-600 text-white' : 'bg-gray-800 text-gray-300'} hover:bg-gray-700`}
                    >
                      Yes
                    </button>
                    <button
                      onClick={() => handleAnswer('contactKnown', false)}
                      className={`p-2 rounded-lg border border-gray-700 ${!answers.contactKnown ? 'bg-blue-600 text-white' : 'bg-gray-800 text-gray-300'} hover:bg-gray-700`}
                    >
                      No
                    </button>
                  </div>
                </div>
              )}
              <button
                onClick={handleSubmit}
                className="w-full p-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
                disabled={!answers.who}
              >
                Check Risk
              </button>
            </div>
          </div>
        )}

        {step === 2 && result && (
          <div>
            <h2 className="text-xl font-semibold text-gray-100 mb-2">{result.riskLevel}</h2>
            <p className="text-base text-gray-300 mb-4">{result.explanation}</p>
            <button
              onClick={reset}
              className="w-full p-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
            >
              Check Another Payment
            </button>
          </div>
        )}
      </div>
      <Footer />
    </div>
  );
}

export default ScamChecker;
