import React from 'react';
import NavBar from '../components/NavBar';
import Footer from '../components/Footer';
import TrendCard from '../components/TrendCard';

function ScamTrends() {
  return (
    <div className="min-h-screen bg-gradient-to-b from-white to-gray-100">
      <NavBar />
      <div className="pt-20 pb-10 max-w-3xl mx-auto px-4">
        <h1 className="text-3xl font-semibold text-gray-900 mt-8 mb-6">Scam Trends</h1>

        <TrendCard
          title="Current Top Scams"
          content={
            <div className="text-base text-gray-600">
              <p><strong>Safe Account Scam:</strong> Fraudsters pose as bank staff, urging you to move money to a “safe” account.</p>
              <p><strong>Crypto Giveaway:</strong> Fake promises of doubled investments if you send cryptocurrency first.</p>
              <p><strong>Parcel Scams:</strong> Texts or emails claiming a delivery fee is due, leading to phishing links.</p>
            </div>
          }
          tag="Trending"
        />

        <TrendCard
          title="New Tactics Emerging"
          content={
            <p className="text-base text-gray-600">
              <strong>AI Voice Scams:</strong> Scammers use voice cloning to impersonate loved ones or officials, requesting urgent funds.
            </p>
          }
          tag="New"
        />

        <TrendCard
          title="Scams Pretending to Be Banks or HMRC"
          content={
            <p className="text-base text-gray-600">
              Fraudsters mimic official emails or calls from banks or HMRC, often citing tax refunds or account issues to extract personal details or payments.
            </p>
          }
          tag="Trending"
        />

        <TrendCard
          title="Examples of Recent Scam Messages"
          content={
            <div className="text-base text-gray-600">
              <p className="italic">“Your package is held at the depot. Pay £2.99 to release it: [link]”</p>
              <p className="italic">“HMRC: You’re due a refund of £342. Click to claim: [link]”</p>
              <p className="text-sm text-gray-500 mt-2">Note: Links sanitized for safety.</p>
            </div>
          }
        />
      </div>
      <Footer />
    </div>
  );
}

export default ScamTrends;
