import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { ThemeContext } from '../App';

function NavBar() {
  const { isDarkMode, toggleDarkMode } = useContext(ThemeContext);

  return (
    <nav className="fixed top-0 w-full bg-white dark:bg-gray-800 shadow-md p-4 z-10">
      <div className="max-w-4xl mx-auto flex justify-between items-center">
        <div className="text-xl font-semibold text-gray-900 dark:text-gray-100">
          <Link to="/">ScamSafe</Link>
        </div>
        <div className="space-x-6 flex items-center">
          <Link to="/" className="text-gray-700 dark:text-gray-300 hover:text-blue-600 dark:hover:text-blue-400">
            Scam Checker
          </Link>
          <Link to="/help-advice" className="text-gray-700 dark:text-gray-300 hover:text-blue-600 dark:hover:text-blue-400">
            Help & Advice
          </Link>
          <Link to="/scam-trends" className="text-gray-700 dark:text-gray-300 hover:text-blue-600 dark:hover:text-blue-400">
            Scam Trends
          </Link>
          <Link to="/fraud-contacts" className="text-gray-700 dark:text-gray-300 hover:text-blue-600 dark:hover:text-blue-400">
            Fraud Contacts
          </Link>
          <button
            onClick={toggleDarkMode}
            className="p-2 rounded-lg bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 hover:bg-gray-300 dark:hover:bg-gray-600"
          >
            {isDarkMode ? 'Light Mode' : 'Dark Mode'}
          </button>
        </div>
      </div>
    </nav>
  );
}

export default NavBar;
