import { motion } from 'framer-motion';

function ResultCard({ riskLevel, explanation, nextSteps }) {
  const riskStyles = {
    Low: 'bg-green-100 text-green-800 border-green-300',
    Medium: 'bg-orange-100 text-orange-800 border-orange-300',
    High: 'bg-red-100 text-red-800 border-red-300',
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
      className={`mt-6 p-6 rounded-xl shadow-lg border ${riskStyles[riskLevel]}`}
    >
      <h3 className="text-lg font-semibold">Risk Level: {riskLevel}</h3>
      <p className="mt-2"><strong>Explanation:</strong> {explanation}</p>
      <p className="mt-2"><strong>Next Steps:</strong> {nextSteps}</p>
    </motion.div>
  );
}

export default ResultCard;
