import { useState } from 'react';
import ResultCard from '../components/ResultCard';

const paymentCategories = [
  'Car Purchase', 'Crypto Payment', 'Friend or Family Request', 'Own Account Transfer',
  'Building Work', 'Investment', 'House Purchase', 'Loan Repayment', 'Multiple Payments', 'Other'
];

const questionFlows = {
  'Car Purchase': [
    { question: 'Where did you find the car?', options: ['Facebook', 'eBay', 'AutoTrader', 'Dealership', 'Other'] },
    { question: 'Have you seen the car or test driven it in person?', options: ['Yes', 'No'] },
    { question: 'Is it a private seller or a dealership?', options: ['Private Seller', 'Dealership'] },
    { question: 'Are you being asked to pay before physically collecting the car?', options: ['Yes', 'No'] },
    { question: 'How did you receive the bank details?', options: ['In person', 'Email/Text', 'Phone'] },
    { question: 'Did the seller contact you first?', options: ['Yes', 'No'] },
    { question: 'Are you being pressured to pay quickly?', options: ['Yes', 'No'] },
    { question: 'Is the payment method bank transfer only?', options: ['Yes', 'No'] },
  ],
  'Crypto Payment': [
    { question: 'Which platform are you sending money to?', options: ['Coinbase', 'Binance', 'Other'] },
    { question: 'Did you open the crypto account yourself?', options: ['Yes', 'No'] },
    { question: 'Does anyone else have access to the account?', options: ['Yes', 'No'] },
    { question: 'Who asked you to make the payment?', options: ['No one', 'Someone online', 'Someone on phone'] },
    { question: 'Are you expecting large returns with little risk?', options: ['Yes', 'No'] },
    { question: 'How did they contact you?', options: ['Email', 'Social media', 'Phone'] },
    { question: 'Have you researched the platform or offer?', options: ['Yes', 'No'] },
    { question: 'Are you being rushed to send the payment?', options: ['Yes', 'No'] },
  ],
  'Friend or Family Request': [
    { question: 'Who is asking for money?', options: ['Friend', 'Family', 'Online contact'] },
    { question: 'Have you met them in real life?', options: ['Yes', 'No'] },
    { question: 'Are you being asked to keep this secret?', options: ['Yes', 'No'] },
    { question: 'Have you sent money to them before?', options: ['Yes', 'No'] },
    { question: 'How did they contact you?', options: ['Phone', 'Text', 'Email', 'Social media'] },
    { question: 'Is this a new number or account?', options: ['Yes', 'No'] },
    { question: 'Are they pressuring you to send it quickly?', options: ['Yes', 'No'] },
    { question: 'What’s the money for?', type: 'text' },
  ],
  'Own Account Transfer': [
    { question: 'Did you open this account yourself?', options: ['Yes', 'No'] },
    { question: 'When was it opened?', options: ['Recently (last month)', 'Over a month ago'] },
    { question: 'Are you moving money because someone told you to?', options: ['Yes', 'No'] },
    { question: 'Has anyone contacted you about your original account being “compromised”?', options: ['Yes', 'No'] },
    { question: 'Are you being told to move money to a "safe account"?', options: ['Yes', 'No'] },
    { question: 'Have you verified with your bank that this is a real instruction?', options: ['Yes', 'No'] },
    { question: 'Are you in full control of both accounts?', options: ['Yes', 'No'] },
    { question: 'Has anyone asked you to lie to the bank about this transaction?', options: ['Yes', 'No'] },
    { question: 'Is this a usual transfer for you?', options: ['Yes', 'No'] },
  ],
  'Building Work': [
    { question: 'What work is being done?', type: 'text' },
    { question: 'Have they started the work or is it still pending?', options: ['Started', 'Pending'] },
    { question: 'Who recommended them, or where did you find them?', options: ['Friend/Family', 'Online', 'No one'] },
    { question: 'Are they a registered business or sole trader?', options: ['Registered', 'Sole trader', 'Unsure'] },
    { question: 'Did they provide a written quote or contract?', options: ['Yes', 'No'] },
    { question: 'Are you being asked to pay in full before work is finished?', options: ['Yes', 'No'] },
    { question: 'Have you seen examples of their previous work or reviews?', options: ['Yes', 'No'] },
    { question: 'Are the bank details linked to a business account?', options: ['Yes', 'No', 'Unsure'] },
    { question: 'Are they using urgency to push the payment?', options: ['Yes', 'No'] },
    { question: 'Have they asked you to lie to the bank?', options: ['Yes', 'No'] },
  ],
  'Investment': [
    { question: 'Who introduced this opportunity to you?', options: ['Friend', 'Online', 'Phone'] },
    { question: 'Is the company FCA regulated?', options: ['Yes', 'No', 'Unsure'] },
    { question: 'Is the expected return unusually high or “guaranteed”?', options: ['Yes', 'No'] },
    { question: 'Have you been pressured to act quickly?', options: ['Yes', 'No'] },
    { question: 'How did they contact you?', options: ['Phone', 'Email', 'Social media'] },
    { question: 'Have you checked online reviews or warnings about the company?', options: ['Yes', 'No'] },
    { question: 'Do they have a landline and registered office?', options: ['Yes', 'No', 'Only mobile'] },
    { question: 'Are you being asked to pay via crypto or bank transfer?', options: ['Yes', 'No'] },
    { question: 'Have they provided written documentation?', options: ['Yes', 'No'] },
    { question: 'Has a known financial advisor recommended them?', options: ['Yes', 'No'] },
  ],
  'House Purchase': [
    { question: 'Have you seen the house in person?', options: ['Yes', 'No'] },
    { question: 'Do you have a signed contract or formal agreement?', options: ['Yes', 'No'] },
    { question: 'Are you paying the money directly to a solicitor or agent?', options: ['Yes', 'No'] },
    { question: 'Did the solicitor or agent contact you, or did you find them?', options: ['You found them', 'They contacted you'] },
    { question: 'Have you independently verified the solicitor’s contact details?', options: ['Yes', 'No'] },
    { question: 'Have you received a completion statement or invoice?', options: ['Yes', 'No'] },
    { question: 'Is the solicitor using a personal email (e.g., Gmail)?', options: ['Yes', 'No'] },
    { question: 'Is there pressure to send the payment urgently?', options: ['Yes', 'No'] },
    { question: 'Are you being asked to lie to your bank?', options: ['Yes', 'No'] },
    { question: 'Is the amount what you expected?', options: ['Yes', 'No'] },
  ],
  'Loan Repayment': [
    { question: 'Is it a known or unknown company?', options: ['Known', 'Unknown'] },
    { question: 'Did you apply for the loan yourself?', options: ['Yes', 'No'] },
    { question: 'Are you being asked to pay a fee before getting funds?', options: ['Yes', 'No'] },
    { question: 'How were you contacted?', options: ['Phone', 'Email', 'Text'] },
    { question: 'Have you verified the company with a regulator?', options: ['Yes', 'No'] },
    { question: 'Is the payment method unusual (e.g., crypto)?', options: ['Yes', 'No'] },
    { question: 'Are they rushing you to pay?', options: ['Yes', 'No'] },
    { question: 'Have they provided a contract?', options: ['Yes', 'No'] },
  ],
  'Multiple Payments': [
    { question: 'Who suggested splitting it up?', options: ['You', 'Them'] },
    { question: 'Is this for one item or several?', options: ['One', 'Several'] },
    { question: 'Are you being rushed to make these payments?', options: ['Yes', 'No'] },
    { question: 'Why are multiple payments needed?', type: 'text' },
    { question: 'Are you paying via bank transfer?', options: ['Yes', 'No'] },
    { question: 'Have they asked you to avoid bank questions?', options: ['Yes', 'No'] },
    { question: 'Is the recipient verified?', options: ['Yes', 'No'] },
    { question: 'Is this a normal process for you?', options: ['Yes', 'No'] },
  ],
  'Other': [
    { question: 'Please describe the payment request:', type: 'text' },
  ],
};

function ScamChecker() {
  const [category, setCategory] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [result, setResult] = useState(null);

  const handleCategorySelect = (selectedCategory) => {
    setCategory(selectedCategory);
    setCurrentQuestionIndex(0);
    setAnswers([]);
    setResult(null);
  };

  const handleAnswer = (answer) => {
    const newAnswers = [...answers, answer];
    setAnswers(newAnswers);

    const questions = questionFlows[category];
    if (currentQuestionIndex + 1 < questions.length) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      evaluateRisk(newAnswers);
    }
  };

  const evaluateRisk = (answers) => {
    let riskLevel, explanation, nextSteps;

    switch (category) {
      case 'Car Purchase':
        if (answers[1] === 'No' && answers[3] === 'Yes' && answers[7] === 'Yes') {
          riskLevel = 'Extremely High';
          explanation = 'Paying before seeing the car via bank transfer is a common scam.';
          nextSteps = 'Do not send payment. Verify the car in person.';
        } else if (answers[1] === 'No' && answers[2] === 'Private Seller') {
          riskLevel = 'High';
          explanation = 'Buying from a private seller without seeing the car is risky.';
          nextSteps = 'Inspect the car before paying.';
        } else if (answers[2] === 'Dealership' && answers[1] === 'No') {
          riskLevel = 'Medium';
          explanation = 'You haven’t seen the car, but it’s from a dealership.';
          nextSteps = 'Verify the dealership and try to see the car.';
        } else {
          riskLevel = 'Low';
          explanation = 'You’ve seen the car and are paying on collection.';
          nextSteps = 'Proceed with caution.';
        }
        break;

      case 'Crypto Payment':
        if (answers[1] === 'No' || answers[2] === 'Yes' || answers[4] === 'Yes') {
          riskLevel = 'Extremely High';
          explanation = 'Lack of control or high-return promises are scam signs.';
          nextSteps = 'Do not proceed. Secure your accounts.';
        } else if (answers[3] === 'Someone online' || answers[6] === 'No') {
          riskLevel = 'High';
          explanation = 'Unsolicited offers or no research raise concerns.';
          nextSteps = 'Research thoroughly before proceeding.';
        } else if (answers[0] === 'Other') {
          riskLevel = 'Medium';
          explanation = 'Using an unknown platform requires caution.';
          nextSteps = 'Verify the platform’s legitimacy.';
        } else {
          riskLevel = 'Low';
          explanation = 'You control your account on a known platform.';
          nextSteps = 'Ensure your account is secure.';
        }
        break;

      case 'Friend or Family Request':
        if (answers[1] === 'No' && answers[2] === 'Yes' && answers[5] === 'Yes') {
          riskLevel = 'Extremely High';
          explanation = 'Unknown contact asking for secrecy from a new source is a scam.';
          nextSteps = 'Verify their identity before sending money.';
        } else if (answers[1] === 'No' || answers[0] === 'Online contact') {
          riskLevel = 'High';
          explanation = 'Unmet or online contacts raise identity doubts.';
          nextSteps = 'Confirm it’s really them.';
        } else if (answers[3] === 'No') {
          riskLevel = 'Medium';
          explanation = 'No prior payments to a known person needs caution.';
          nextSteps = 'Double-check their request.';
        } else {
          riskLevel = 'Low';
          explanation = 'You know them well and it’s not unusual.';
          nextSteps = 'Proceed if you’re comfortable.';
        }
        break;

      case 'Own Account Transfer':
        if (answers[4] === 'Yes' || answers[7] === 'Yes') {
          riskLevel = 'Extremely High';
          explanation = '“Safe account” or lying to the bank is a scam tactic.';
          nextSteps = 'Do not move money. Call your bank directly.';
        } else if (answers[0] === 'No' || answers[2] === 'Yes') {
          riskLevel = 'High';
          explanation = 'An account you didn’t open or external advice is suspicious.';
          nextSteps = 'Verify with your bank.';
        } else if (answers[1] === 'Recently (last month)' && answers[6] === 'No') {
          riskLevel = 'Medium';
          explanation = 'New account without full control needs checking.';
          nextSteps = 'Confirm both accounts are secure.';
        } else {
          riskLevel = 'Low';
          explanation = 'Transferring between your own accounts is normal.';
          nextSteps = 'Ensure your accounts are secure.';
        }
        break;

      case 'Building Work':
        if (answers[5] === 'Yes' && answers[9] === 'Yes') {
          riskLevel = 'Extremely High';
          explanation = 'Full payment upfront with pressure to lie is a scam.';
          nextSteps = 'Do not pay until work begins. Report to authorities.';
        } else if (answers[3] === 'Unsure' && answers[5] === 'Yes') {
          riskLevel = 'High';
          explanation = 'Unverified company asking for full payment is risky.';
          nextSteps = 'Verify their credentials first.';
        } else if (answers[1] === 'Pending' && answers[6] === 'No') {
          riskLevel = 'Medium';
          explanation = 'No work started and no reviews need caution.';
          nextSteps = 'Pay incrementally as work progresses.';
        } else {
          riskLevel = 'Low';
          explanation = 'Work started by a known company is safer.';
          nextSteps = 'Proceed with payment as agreed.';
        }
        break;

      case 'Investment':
        if (answers[2] === 'Yes' && answers[3] === 'Yes' && answers[1] === 'No') {
          riskLevel = 'Extremely High';
          explanation = 'High returns with urgency from an unregulated firm is a scam.';
          nextSteps = 'Do not invest. Report to FCA.';
        } else if (answers[4] === 'Phone' && answers[5] === 'No') {
          riskLevel = 'High';
          explanation = 'Unsolicited contact with no research is risky.';
          nextSteps = 'Verify regulation status.';
        } else if (answers[1] === 'Unsure' && answers[7] === 'Yes') {
          riskLevel = 'Medium';
          explanation = 'Unverified regulation with unusual payment needs checking.';
          nextSteps = 'Confirm with FCA before investing.';
        } else {
          riskLevel = 'Low';
          explanation = 'Regulated firm with no pressure is safer.';
          nextSteps = 'Proceed with due diligence.';
        }
        break;

      case 'House Purchase':
        if (answers[0] === 'No' && answers[2] === 'No' && answers[7] === 'Yes') {
          riskLevel = 'Extremely High';
          explanation = 'No house visit, no solicitor, and urgency are scam signs.';
          nextSteps = 'Do not pay. Verify with a solicitor you contacted.';
        } else if (answers[1] === 'No' || answers[4] === 'No') {
          riskLevel = 'High';
          explanation = 'No contract or unverified solicitor raises concerns.';
          nextSteps = 'Ensure a verified solicitor handles payment.';
        } else if (answers[0] === 'Yes' && answers[3] === 'They contacted you') {
          riskLevel = 'Medium';
          explanation = 'House seen, but solicitor contact needs verification.';
          nextSteps = 'Independently verify the solicitor’s details.';
        } else {
          riskLevel = 'Low';
          explanation = 'Verified solicitor you found and house seen are safe.';
          nextSteps = 'Proceed with payment as agreed.';
        }
        break;

      case 'Loan Repayment':
        if (answers[2] === 'Yes' || answers[4] === 'No') {
          riskLevel = 'Extremely High';
          explanation = 'Fees before funds or unverified company are scam tactics.';
          nextSteps = 'Do not pay. Report to authorities.';
        } else if (answers[0] === 'Unknown' && answers[1] === 'No') {
          riskLevel = 'High';
          explanation = 'Unknown company not applied to is suspicious.';
          nextSteps = 'Verify the lender’s legitimacy.';
        } else if (answers[3] === 'Phone' && answers[5] === 'Yes') {
          riskLevel = 'Medium';
          explanation = 'Unsolicited contact with unusual method needs caution.';
          nextSteps = 'Confirm with the lender.';
        } else {
          riskLevel = 'Low';
          explanation = 'Known company you applied to is safer.';
          nextSteps = 'Proceed if verified.';
        }
        break;

      case 'Multiple Payments':
        if (answers[0] === 'Them' && answers[2] === 'Yes' && answers[5] === 'Yes') {
          riskLevel = 'Extremely High';
          explanation = 'Splitting payments under pressure to avoid bank flags is a scam.';
          nextSteps = 'Do not proceed. Contact your bank.';
        } else if (answers[0] === 'Them' && answers[1] === 'One') {
          riskLevel = 'High';
          explanation = 'Splitting for one item by their request is suspicious.';
          nextSteps = 'Question the recipient’s intent.';
        } else if (answers[6] === 'No') {
          riskLevel = 'Medium';
          explanation = 'Unverified recipient for multiple payments needs clarity.';
          nextSteps = 'Ensure transparency.';
        } else {
          riskLevel = 'Low';
          explanation = 'Your choice to split payments is fine.';
          nextSteps = 'Proceed as planned.';
        }
        break;

      case 'Other':
        riskLevel = 'Medium';
        explanation = 'Limited details provided; more analysis needed.';
        nextSteps = 'Use "Chat with Advisor" for a deeper check (coming soon).';
        break;

      default:
        riskLevel = 'Medium';
        explanation = 'Unable to fully assess with current info.';
        nextSteps = 'Provide more details for accuracy.';
    }

    setResult({
      riskLevel,
      explanation,
      nextSteps: `${nextSteps} Contact Action Fraud (0300 123 2040) if unsure.`,
    });
  };

  const currentQuestion = category && questionFlows[category][currentQuestionIndex];

  return (
    <div className="pt-20 pb-10 max-w-3xl mx-auto px-4">
      <h1 className="text-3xl font-bold text-gray-800">Payment Scam Checker</h1>
      <p className="mt-2 text-gray-600">Let’s check if your payment request is safe.</p>

      {!category && (
        <div className="mt-6">
          <h2 className="text-lg font-semibold">What type of payment are you making?</h2>
          <div className="mt-4 grid grid-cols-2 sm:grid-cols-3 gap-4">
            {paymentCategories.map((cat) => (
              <button
                key={cat}
                onClick={() => handleCategorySelect(cat)}
                className="p-3 bg-blue-100 text-blue-800 rounded-xl hover:bg-blue-200 transition"
              >
                {cat}
              </button>
            ))}
          </div>
        </div>
      )}

      {category && !result && (
        <div className="mt-6">
          <h2 className="text-lg font-semibold">{currentQuestion.question}</h2>
          <div className="mt-4">
            {currentQuestion.options ? (
              currentQuestion.options.map((option) => (
                <button
                  key={option}
                  onClick={() => handleAnswer(option)}
                  className="mr-2 mb-2 px-4 py-2 bg-gray-200 rounded-lg hover:bg-gray-300"
                >
                  {option}
                </button>
              ))
            ) : (
              <input
                type="text"
                onKeyPress={(e) => e.key === 'Enter' && handleAnswer(e.target.value)}
                className="w-full p-3 rounded-xl border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="Type your answer and press Enter"
              />
            )}
          </div>
        </div>
      )}

      {result && (
        <div className="mt-6">
          <ResultCard {...result} />
          <div className="mt-4 flex space-x-4">
            <button onClick={() => setCategory(null)} className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700">
              Start Over
            </button>
            <button className="px-6 py-2 bg-gray-300 text-gray-800 rounded-lg opacity-50 cursor-not-allowed" disabled>
              Chat with Advisor (Coming Soon)
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default ScamChecker;
