import { useState } from 'react';
import ResultCard from '../components/ResultCard';

const paymentCategories = [
  'Car Purchase', 'Crypto Payment', 'Friend or Family Request', 'Own Account Transfer',
  'Building Work', 'Investment', 'House Purchase', 'Loan Repayment', 'Multiple Payments', 'Other'
];

const questionFlows = {
  'Car Purchase': [
    { question: 'Where did you find the car?', options: ['Facebook', 'eBay', 'AutoTrader', 'Dealership', 'Private Seller', 'Other'] },
    { question: 'Have you seen the car or test driven it in person?', options: ['Yes', 'No'] },
    // Skip redundant seller type question if already answered in Q1
    { question: 'Is it a private seller or a dealership?', options: ['Private Seller', 'Dealership'], conditional: (answers) => !['Dealership', 'Private Seller'].includes(answers[0]) },
    { question: 'Are you being asked to pay before physically collecting the car?', options: ['Yes', 'No'] },
    { question: 'How did you receive the bank details?', options: ['In person', 'Email/Text', 'Phone'] },
    { question: 'Did the seller contact you first?', options: ['Yes', 'No'] },
    { question: 'Are you being pressured to pay quickly?', options: ['Yes', 'No'] },
    { question: 'Is the payment method bank transfer only?', options: ['Yes', 'No'] },
  ],
  'Crypto Payment': [
    { question: 'Which platform are you sending money to?', options: ['Coinbase', 'Binance', 'Other'] },
    { question: 'Did you open the crypto account yourself?', options: ['Yes', 'No'] },
    { question: 'Does anyone else have access to the account?', options: ['Yes', 'No'] },
    { question: 'Who asked you to make the payment?', options: ['No one', 'Someone online', 'Someone on phone'] },
    { question: 'Are you expecting large returns with little risk?', options: ['Yes', 'No'] },
    { question: 'How did they contact you?', options: ['Email', 'Social media', 'Phone'] },
    { question: 'Have you researched the platform or offer?', options: ['Yes', 'No'] },
    { question: 'Are you being rushed to send the payment?', options: ['Yes', 'No'] },
  ],
  'Friend or Family Request': [
    { question: 'Who is asking for money?', options: ['Friend', 'Family', 'Online contact'] },
    { question: 'Have you met them in real life?', options: ['Yes', 'No'] },
    { question: 'Are you being asked to keep this secret?', options: ['Yes', 'No'] },
    { question: 'Have you sent money to them before?', options: ['Yes', 'No'] },
    { question: 'How did they contact you?', options: ['Phone', 'Text', 'Email', 'Social media'] },
    { question: 'Is this a new number or account?', options: ['Yes', 'No'] },
    { question: 'Are they pressuring you to send it quickly?', options: ['Yes', 'No'] },
    { question: 'What’s the money for?', type: 'text' },
  ],
  'Own Account Transfer': [
    { question: 'Did you open this account yourself?', options: ['Yes', 'No'] },
    { question: 'When was it opened?', options: ['Recently (last month)', 'Over a month ago'] },
    { question: 'Are you moving money because someone told you to?', options: ['Yes', 'No'] },
    { question: 'Has anyone contacted you about your original account being “compromised”?', options: ['Yes', 'No'] },
    { question: 'Are you being told to move money to a "safe account"?', options: ['Yes', 'No'] },
    { question: 'Have you verified with your bank that this is a real instruction?', options: ['Yes', 'No'] },
    { question: 'Are you in full control of both accounts?', options: ['Yes', 'No'] },
    { question: 'Has anyone asked you to lie to the bank about this transaction?', options: ['Yes', 'No'] },
    { question: 'Is this a usual transfer for you?', options: ['Yes', 'No'] },
  ],
  'Building Work': [
    { question: 'What work is being done?', type: 'text' },
    { question: 'Have they started the work or is it still pending?', options: ['Started', 'Pending'] },
    { question: 'Who recommended them, or where did you find them?', options: ['Friend/Family', 'Online', 'No one'] },
    { question: 'Are they a registered business or sole trader?', options: ['Registered', 'Sole trader', 'Unsure'] },
    { question: 'Did they provide a written quote or contract?', options: ['Yes', 'No'] },
    { question: 'Are you being asked to pay in full before work is finished?', options: ['Yes', 'No'] },
    { question: 'Have you seen examples of their previous work or reviews?', options: ['Yes', 'No'] },
    { question: 'Are the bank details linked to a business account?', options: ['Yes', 'No', 'Unsure'] },
    { question: 'Are they using urgency to push the payment?', options: ['Yes', 'No'] },
    { question: 'Have they asked you to lie to the bank?', options: ['Yes', 'No'] },
  ],
  'Investment': [
    { question: 'Who introduced this opportunity to you?', options: ['Friend', 'Online', 'Phone'] },
    { question: 'Is the company FCA regulated?', options: ['Yes', 'No', 'Unsure'] },
    { question: 'Is the expected return unusually high or “guaranteed”?', options: ['Yes', 'No'] },
    { question: 'Have you been pressured to act quickly?', options: ['Yes', 'No'] },
    { question: 'How did they contact you?', options: ['Phone', 'Email', 'Social media'] },
    { question: 'Have you checked online reviews or warnings about the company?', options: ['Yes', 'No'] },
    { question: 'Do they have a landline and registered office?', options: ['Yes', 'No', 'Only mobile'] },
    { question: 'Are you being asked to pay via crypto or bank transfer?', options: ['Yes', 'No'] },
    { question: 'Have they provided written documentation?', options: ['Yes', 'No'] },
    { question: 'Has a known financial advisor recommended them?', options: ['Yes', 'No'] },
  ],
  'House Purchase': [
    { question: 'Have you seen the house in person?', options: ['Yes', 'No'] },
    { question: 'Who are you sending the payment to?', options: ['UK Solicitor / Law Firm', 'UK Estate Agent', 'Overseas Lawyer / Legal Entity', 'Individual Seller (Overseas)', 'Not Sure / Unknown'] },
    { question: 'Do you have a signed contract or formal agreement?', options: ['Yes', 'No'] },
    { question: 'Did you find them, or did they contact you?', options: ['You found them', 'They contacted you'] },
    { question: 'Have you independently verified their contact details?', options: ['Yes', 'No'] },
    { question: 'Is there pressure to send the payment urgently?', options: ['Yes', 'No'] },
    { question: 'Are you being asked to lie to your bank?', options: ['Yes', 'No'] },
    { question: 'Is the amount what you expected?', options: ['Yes', 'No'] },
    { question: 'Is this payment via bank transfer?', options: ['Yes', 'No'] },
    { question: 'Is there a legal agreement for this sale?', options: ['Yes', 'No'], conditional: (answers) => answers[1] === 'Individual Seller (Overseas)' },
    { question: 'Have you verified the seller’s identity and ownership?', options: ['Yes', 'No'], conditional: (answers) => answers[1] === 'Individual Seller (Overseas)' },
    { question: 'Have you received independent legal advice?', options: ['Yes', 'No'], conditional: (answers) => answers[1] === 'Individual Seller (Overseas)' },
    { question: 'Are you being asked to use crypto or cash?', options: ['Yes', 'No'], conditional: (answers) => answers[1] === 'Individual Seller (Overseas)' },
  ],
  'Loan Repayment': [
    { question: 'Is it a known or unknown company?', options: ['Known', 'Unknown'] },
    { question: 'Did you apply for the loan yourself?', options: ['Yes', 'No'] },
    { question: 'Are you being asked to pay a fee before getting funds?', options: ['Yes', 'No'] },
    { question: 'How were you contacted?', options: ['Phone', 'Email', 'Text'] },
    { question: 'Have you verified the company with a regulator?', options: ['Yes', 'No'] },
    { question: 'Is the payment method unusual (e.g., crypto)?', options: ['Yes', 'No'] },
    { question: 'Are they rushing you to pay?', options: ['Yes', 'No'] },
    { question: 'Have they provided a contract?', options: ['Yes', 'No'] },
  ],
  'Multiple Payments': [
    { question: 'Who suggested splitting it up?', options: ['You', 'Them'] },
    { question: 'Is this for one item or several?', options: ['One', 'Several'] },
    { question: 'Are you being rushed to make these payments?', options: ['Yes', 'No'] },
    { question: 'Why are multiple payments needed?', type: 'text' },
    { question: 'Are you paying via bank transfer?', options: ['Yes', 'No'] },
    { question: 'Have they asked you to avoid bank questions?', options: ['Yes', 'No'] },
    { question: 'Is the recipient verified?', options: ['Yes', 'No'] },
    { question: 'Is this a normal process for you?', options: ['Yes', 'No'] },
  ],
  'Other': [
    { question: 'Please describe the payment request:', type: 'text' },
  ],
};

function ScamChecker() {
  const [category, setCategory] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [result, setResult] = useState(null);

  const handleCategorySelect = (selectedCategory) => {
    setCategory(selectedCategory);
    setCurrentQuestionIndex(0);
    setAnswers([]);
    setResult(null);
  };

  const handleAnswer = (answer) => {
    const newAnswers = [...answers, answer];
    setAnswers(newAnswers);

    const questions = questionFlows[category];
    const nextIndex = currentQuestionIndex + 1;
    if (nextIndex < questions.length && (!questions[nextIndex].conditional || questions[nextIndex].conditional(newAnswers))) {
      setCurrentQuestionIndex(nextIndex);
    } else {
      evaluateRisk(newAnswers);
    }
  };

  const evaluateRisk = (answers) => {
    let riskLevel, explanation, nextSteps;

    switch (category) {
      case 'Car Purchase':
        const sellerType = answers[0] === 'Dealership' || answers[2] === 'Dealership' ? 'Dealership' : 
                          answers[0] === 'Private Seller' || answers[2] === 'Private Seller' ? 'Private Seller' : 'Unknown';
        if (sellerType === 'Private Seller' && answers[1] === 'No') {
          riskLevel = 'Extremely High';
          explanation = 'Paying a private seller without seeing the car is a strong scam indicator.';
          nextSteps = 'This is a high-risk situation. Consider pausing to inspect the car in person.';
        } else if (sellerType === 'Dealership' && answers[3] === 'Yes') {
          if (answers[1] === 'No' && answers[6] === 'Yes') {
            riskLevel = 'Extremely High';
            explanation = 'Paying a dealership before collection with no test drive and urgency raises red flags.';
            nextSteps = 'This looks risky. Consider verifying the dealership and seeing the car first.';
          } else {
            riskLevel = 'High';
            explanation = 'Paying a dealership before collection is unusual and carries risk.';
            nextSteps = 'This is a concern. Consider verifying the dealership’s legitimacy.';
          }
        } else if (answers[1] === 'No') {
          riskLevel = 'High';
          explanation = 'Not seeing the car before payment is a significant risk factor.';
          nextSteps = 'This raises concerns. Consider pausing to see the car first.';
        } else {
          riskLevel = 'Low';
          explanation = 'No major red flags detected with this purchase.';
          nextSteps = 'This looks safer, but always double-check details.';
        }
        break;

      case 'Crypto Payment':
        if (answers[1] === 'No' || answers[2] === 'Yes' || answers[4] === 'Yes') {
          riskLevel = 'Extremely High';
          explanation = 'Lack of control or high-return promises are strong scam indicators.';
          nextSteps = 'This raises serious concerns. Consider securing your accounts.';
        } else if (answers[3] === 'Someone online' || answers[6] === 'No') {
          riskLevel = 'High';
          explanation = 'Unsolicited offers or no research are notable risks.';
          nextSteps = 'This is concerning. Researching further might help.';
        } else if (answers[0] === 'Other') {
          riskLevel = 'Medium';
          explanation = 'Using an unknown platform carries some risk.';
          nextSteps = 'This needs caution. Checking the platform’s legitimacy could help.';
        } else {
          riskLevel = 'Low';
          explanation = 'No major red flags detected here.';
          nextSteps = 'This looks safer, but keep your account secure.';
        }
        break;

      case 'Friend or Family Request':
        if (answers[1] === 'No' && answers[2] === 'Yes' && answers[5] === 'Yes') {
          riskLevel = 'Extremely High';
          explanation = 'An unknown contact asking for secrecy from a new source is a strong scam sign.';
          nextSteps = 'This looks very risky. Verifying their identity might be wise.';
        } else if (answers[1] === 'No' || answers[0] === 'Online contact') {
          riskLevel = 'High';
          explanation = 'Unmet or online contacts raise identity concerns.';
          nextSteps = 'This is a concern. Confirming it’s really them could help.';
        } else if (answers[3] === 'No') {
          riskLevel = 'Medium';
          explanation = 'No prior payments to a known person carries some risk.';
          nextSteps = 'This needs caution. Double-checking their request might be useful.';
        } else {
          riskLevel = 'Low';
          explanation = 'No major red flags detected with this request.';
          nextSteps = 'This looks safer, but stay mindful.';
        }
        break;

      case 'Own Account Transfer':
        if (answers[4] === 'Yes' || answers[7] === 'Yes') {
          riskLevel = 'Extremely High';
          explanation = '“Safe account” mentions or requests to lie are strong scam indicators.';
          nextSteps = 'This raises serious concerns. Checking with your bank directly might help.';
        } else if (answers[0] === 'No' || answers[2] === 'Yes') {
          riskLevel = 'High';
          explanation = 'An account you didn’t open or external advice is a notable risk.';
          nextSteps = 'This is concerning. Verifying with your bank could clarify.';
        } else if (answers[1] === 'Recently (last month)' && answers[6] === 'No') {
          riskLevel = 'Medium';
          explanation = 'A new account without full control carries some risk.';
          nextSteps = 'This needs caution. Confirming account security might help.';
        } else {
          riskLevel = 'Low';
          explanation = 'No major red flags detected with this transfer.';
          nextSteps = 'This looks safer, but keep your accounts secure.';
        }
        break;

      case 'Building Work':
        if (answers[1] === 'Pending' && answers[5] === 'Yes' && answers[9] === 'Yes') {
          riskLevel = 'Extremely High';
          explanation = 'Full payment before work starts with a request to lie is a strong scam sign.';
          nextSteps = 'This raises serious concerns. Verifying the builder might be wise.';
        } else if (answers[1] === 'Pending' && answers[5] === 'Yes') {
          riskLevel = 'High';
          explanation = 'Paying in full before work starts is a notable risk, even with a trusted builder.';
          nextSteps = 'This is concerning. Some builders ask for deposits, but verify with a written quote.';
        } else if (answers[1] === 'Pending') {
          riskLevel = 'Medium';
          explanation = 'Payment before work starts carries some risk, even if not full payment.';
          nextSteps = 'This needs caution. Deposits are common, but ensure a quote and verification.';
        } else if (answers[5] === 'Yes' && answers[4] === 'No') {
          riskLevel = 'High';
          explanation = 'Paying in full without a contract is a significant risk.';
          nextSteps = 'This raises concerns. A written quote could reduce risk.';
        } else {
          riskLevel = 'Low';
          explanation = 'No major red flags detected with this payment.';
          nextSteps = 'This looks safer, but always verify details.';
        }
        break;

      case 'Investment':
        if (answers[2] === 'Yes' && answers[3] === 'Yes' && answers[1] === 'No') {
          riskLevel = 'Extremely High';
          explanation = 'High returns with urgency from an unregulated firm are strong scam signs.';
          nextSteps = 'This raises serious concerns. Checking with the FCA might help.';
        } else if (answers[4] === 'Phone' && answers[5] === 'No') {
          riskLevel = 'High';
          explanation = 'Unsolicited contact with no research is a notable risk.';
          nextSteps = 'This is concerning. Verifying regulation status could clarify.';
        } else if (answers[1] === 'Unsure' && answers[7] === 'Yes') {
          riskLevel = 'Medium';
          explanation = 'Unverified regulation with unusual payment carries some risk.';
          nextSteps = 'This needs caution. Confirming with the FCA might help.';
        } else {
          riskLevel = 'Low';
          explanation = 'No major red flags detected with this investment.';
          nextSteps = 'This looks safer, but keep researching.';
        }
        break;

      case 'House Purchase':
        if (answers[1] === 'Not Sure / Unknown' || (answers[0] === 'No' && answers[4] === 'No' && answers[5] === 'Yes')) {
          riskLevel = 'Extremely High';
          explanation = 'Unknown recipient or no house visit with urgency are strong scam signs.';
          nextSteps = 'This raises serious concerns. Verifying with a solicitor might help.';
        } else if (answers[1] === 'Individual Seller (Overseas)') {
          if (answers[9] === 'No' || answers[10] === 'No' || answers[12] === 'Yes') {
            riskLevel = 'Extremely High';
            explanation = 'Overseas seller with no contract, verification, or crypto pressure is a strong scam risk.';
            nextSteps = 'This looks very risky. Seeking legal advice might help.';
          } else if (answers[5] === 'Yes' || answers[6] === 'Yes') {
            riskLevel = 'High';
            explanation = 'Overseas seller with urgency or secrecy raises notable concerns.';
            nextSteps = 'This is concerning. Verifying identity and ownership could clarify.';
          } else if (answers[9] === 'Yes' && answers[10] === 'Yes' && answers[11] === 'Yes') {
            riskLevel = 'Medium';
            explanation = 'Overseas seller with legal process carries some risk but seems normal.';
            nextSteps = 'This needs caution. Ensuring all documents are legitimate might help.';
          } else {
            riskLevel = 'High';
            explanation = 'Overseas seller without full verification is a notable risk.';
            nextSteps = 'This raises concerns. Confirming ownership and advice could help.';
          }
        } else if (answers[1] === 'UK Solicitor / Law Firm' || answers[1] === 'UK Estate Agent') {
          if (answers[0] === 'No' || answers[2] === 'No') {
            riskLevel = 'High';
            explanation = 'No house visit or contract with a UK professional is a notable risk.';
            nextSteps = 'This is concerning. Verifying the house and agreement might help.';
          } else if (answers[3] === 'They contacted you' && answers[4] === 'No') {
            riskLevel = 'Medium';
            explanation = 'Solicitor contacted you without verification carries some risk.';
            nextSteps = 'This needs caution. Independently confirming their credentials could help.';
          } else {
            riskLevel = 'Low';
            explanation = 'No major red flags detected with this payment.';
            nextSteps = 'This looks safer, but double-check details.';
          }
        } else if (answers[1] === 'Overseas Lawyer / Legal Entity') {
          if (answers[2] === 'No' || answers[4] === 'No') {
            riskLevel = 'High';
            explanation = 'No contract or unverified overseas lawyer is a notable risk.';
            nextSteps = 'This raises concerns. Verifying their credentials might help.';
          } else if (answers[5] === 'Yes') {
            riskLevel = 'Medium';
            explanation = 'Overseas lawyer with urgency carries some risk.';
            nextSteps = 'This needs caution. Double-checking their legitimacy could help.';
          } else {
            riskLevel = 'Low';
            explanation = 'No major red flags detected with this payment.';
            nextSteps = 'This looks safer, but ensure all details align.';
          }
        }
        break;

      case 'Loan Repayment':
        if (answers[2] === 'Yes' || answers[4] === 'No') {
          riskLevel = 'Extremely High';
          explanation = 'Fees before funds or unverified company are strong scam signs.';
          nextSteps = 'This raises serious concerns. Checking with authorities might help.';
        } else if (answers[0] === 'Unknown' && answers[1] === 'No') {
          riskLevel = 'High';
          explanation = 'Unknown company not applied to is a notable risk.';
          nextSteps = 'This is concerning. Verifying the lender’s legitimacy could clarify.';
        } else if (answers[3] === 'Phone' && answers[5] === 'Yes') {
          riskLevel = 'Medium';
          explanation = 'Unsolicited contact with unusual method carries some risk.';
          nextSteps = 'This needs caution. Confirming with the lender might help.';
        } else {
          riskLevel = 'Low';
          explanation = 'No major red flags detected with this repayment.';
          nextSteps = 'This looks safer, but stay mindful.';
        }
        break;

      case 'Multiple Payments':
        if (answers[0] === 'Them' && answers[2] === 'Yes' && answers[5] === 'Yes') {
          riskLevel = 'Extremely High';
          explanation = 'Splitting payments under pressure to avoid bank flags is a strong scam sign.';
          nextSteps = 'This raises serious concerns. Checking with your bank might help.';
        } else if (answers[0] === 'Them' && answers[1] === 'One') {
          riskLevel = 'High';
          explanation = 'Splitting for one item by their request is a notable risk.';
          nextSteps = 'This is concerning. Questioning their intent could clarify.';
        } else if (answers[6] === 'No') {
          riskLevel = 'Medium';
          explanation = 'Unverified recipient for multiple payments carries some risk.';
          nextSteps = 'This needs caution. Ensuring transparency might help.';
        } else {
          riskLevel = 'Low';
          explanation = 'No major red flags detected with this payment.';
          nextSteps = 'This looks safer, but verify details.';
        }
        break;

      case 'Other':
        riskLevel = 'Medium';
        explanation = 'Limited details provided; more analysis might reveal risks.';
        nextSteps = 'This needs caution. More details could improve accuracy.';
        break;

      default:
        riskLevel = 'Medium';
        explanation = 'Unable to fully assess with current info.';
        nextSteps = 'This needs caution. Providing more details might help.';
    }

    setResult({
      riskLevel,
      explanation: `${explanation} You are in control. This result is for insight only. We recommend pausing and verifying if you have any doubts.`,
      nextSteps: `${nextSteps} Contact Action Fraud (0300 123 2040) if unsure.`,
    });
  };

  const currentQuestion = category && questionFlows[category].find((q, i) => i === currentQuestionIndex && (!q.conditional || q.conditional(answers)));

  return (
    <div className="pt-20 pb-10 max-w-3xl mx-auto px-4">
      <h1 className="text-3xl font-bold text-gray-800">Payment Scam Checker</h1>
      <p className="mt-2 text-gray-600">Let’s check if your payment request raises any red flags.</p>

      {!category && (
        <div className="mt-6">
          <h2 className="text-lg font-semibold">What type of payment are you making?</h2>
          <div className="mt-4 grid grid-cols-2 sm:grid-cols-3 gap-4">
            {paymentCategories.map((cat) => (
              <button
                key={cat}
                onClick={() => handleCategorySelect(cat)}
                className="p-3 bg-blue-100 text-blue-800 rounded-xl hover:bg-blue-200 transition"
              >
                {cat}
              </button>
            ))}
          </div>
        </div>
      )}

      {category && !result && currentQuestion && (
        <div className="mt-6">
          <h2 className="text-lg font-semibold">{currentQuestion.question}</h2>
          <div className="mt-4">
            {currentQuestion.options ? (
              currentQuestion.options.map((option) => (
                <button
                  key={option}
                  onClick={() => handleAnswer(option)}
                  className="mr-2 mb-2 px-4 py-2 bg-gray-200 rounded-lg hover:bg-gray-300"
                >
                  {option}
                </button>
              ))
            ) : (
              <input
                type="text"
                onKeyPress={(e) => e.key === 'Enter' && handleAnswer(e.target.value)}
                className="w-full p-3 rounded-xl border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="Type your answer and press Enter"
              />
            )}
          </div>
        </div>
      )}

      {result && (
        <div className="mt-6">
          <ResultCard {...result} />
          <div className="mt-4 flex space-x-4">
            <button onClick={() => setCategory(null)} className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700">
              Start Over
            </button>
