import { useState } from 'react';
import ResultCard from '../components/ResultCard';

function ScamChecker() {
  const [input, setInput] = useState('');
  const [result, setResult] = useState(null);

  const handleCheck = () => {
    // Placeholder logic for UI demo
    const randomResult = Math.random();
    let riskLevel, explanation, nextSteps;

    if (randomResult < 0.33) {
      riskLevel = 'Low';
      explanation = 'No obvious scam patterns detected.';
      nextSteps = 'Proceed with caution and verify details.';
    } else if (randomResult < 0.66) {
      riskLevel = 'Medium';
      explanation = 'Some suspicious elements found.';
      nextSteps = 'Double-check the source before sending money.';
    } else {
      riskLevel = 'High';
      explanation = 'Strong indicators of a scam.';
      nextSteps = 'Do not send money. Report this immediately.';
    }

    setResult({ riskLevel, explanation, nextSteps });
  };

  return (
    <div className="pt-20 pb-10 max-w-3xl mx-auto px-4">
      <h1 className="text-3xl font-bold text-gray-800">Payment Scam Checker</h1>
      <p className="mt-2 text-gray-600">Paste a payment request, seller message, or investment offer to check if itâ€™s suspicious.</p>
      <textarea
        value={input}
        onChange={(e) => setInput(e.target.value)}
        className="w-full mt-4 p-4 rounded-xl border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
        rows="5"
        placeholder="e.g., 'Hi Mum, can you pay this bill for me?'"
      />
      <div className="mt-4 flex space-x-4">
        <button onClick={handleCheck} className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700">
          Check for Scam
        </button>
        <button onClick={() => setInput('')} className="px-6 py-2 bg-gray-300 text-gray-800 rounded-lg hover:bg-gray-400">
          Clear
        </button>
      </div>
      {result && <ResultCard {...result} />}
    </div>
  );
}

export default ScamChecker;
