import AdviceCard from '../components/AdviceCard';

function HelpAdvice() {
  const advice = [
    { icon: 'üí∏', title: 'I already sent the money', description: 'Steps to recover funds or report fraud.' },
    { icon: 'üì©', title: 'I‚Äôm being asked to pay', description: 'How to verify payment requests safely.' },
    { icon: 'üö®', title: 'How do I report a scam?', description: 'Contact authorities and platforms.' },
    { icon: 'üõ°Ô∏è', title: 'Types of scams to avoid', description: 'Learn common scam tactics.' },
  ];

  return (
    <div className="pt-20 pb-10 max-w-5xl mx-auto px-4">
      <h1 className="text-3xl font-bold text-gray-800">Help & Advice</h1>
      <div className="mt-6 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
        {advice.map((item) => (
          <AdviceCard key={item.title} {...item} />
        ))}
      </div>
    </div>
  );
}

export default HelpAdvice;
