import React, { useState } from 'react';
import NavBar from '../components/NavBar';
import Footer from '../components/Footer';

function ScamChecker() {
  const [formData, setFormData] = useState({
    contactMethod: '',
    senderIdentity: '',
    urgency: '',
    paymentRequest: '',
    contactKnown: '',
    secrecy: '',
  });
  const [result, setResult] = useState(null);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    let riskLevel = '';
    let explanation = '';

    if (formData.senderIdentity === 'friendOrFamily') {
      if (formData.contactKnown === 'no' && formData.paymentRequest === 'yes') {
        riskLevel = 'Extremely High Risk';
        explanation =
          'This may be an impersonation scam (e.g., "Hi Mum/Dad" scam). Never send money unless you’ve confirmed the person’s identity using their usual number or voice call.';
      } else if (formData.contactKnown === 'no' && formData.urgency === 'yes') {
        riskLevel = 'High Risk';
        explanation =
          'An urgent request from an unknown contact claiming to be a loved one is suspicious. Verify their identity before acting.';
      } else if (formData.contactKnown === 'yes' && formData.paymentRequest === 'yes' && formData.secrecy === 'yes') {
        riskLevel = 'Moderate Risk';
        explanation =
          'Even if the contact is known, requests to keep it secret and send money are unusual. Double-check directly with them.';
      } else if (formData.contactKnown === 'yes') {
        riskLevel = 'Low Risk';
        explanation = 'If the contact is verified as a known friend or family member, this is likely safe, but always confirm unusual requests.';
      } else {
        riskLevel = 'Moderate Risk';
        explanation = 'Verify the sender’s identity before proceeding, especially if the request seems unusual.';
      }
    } else if (formData.senderIdentity === 'business') {
      if (formData.paymentRequest === 'yes' && formData.urgency === 'yes' && formData.secrecy === 'yes') {
        riskLevel = 'High Risk';
        explanation =
          'Urgent payment requests from a business, especially with secrecy, are red flags. Verify through official channels before acting.';
      } else if (formData.paymentRequest === 'yes' && formData.contactMethod === 'email') {
        riskLevel = 'Moderate Risk';
        explanation =
          'Email payment requests from businesses can be phishing attempts. Confirm via a known phone number or website.';
      } else if (formData.paymentRequest === 'yes') {
        riskLevel = 'Moderate Risk';
        explanation = 'Payment requests from businesses should be verified through official contact details.';
      } else {
        riskLevel = 'Low Risk';
        explanation = 'No immediate red flags, but verify any unexpected business contact.';
      }
    } else if (formData.senderIdentity === 'unknown') {
      if (formData.paymentRequest === 'yes' && formData.urgency === 'yes') {
        riskLevel = 'High Risk';
        explanation =
          'Urgent payment requests from unknown sources are a common scam tactic. Do not proceed without independent verification.';
      } else if (formData.paymentRequest === 'yes') {
        riskLevel = 'Moderate Risk';
        explanation = 'Payment requests from unknown contacts are suspicious. Verify before acting.';
      } else if (formData.contactMethod === 'text' && formData.urgency === 'yes') {
        riskLevel = 'Moderate Risk';
        explanation = 'Urgent texts from unknown numbers could be smishing attempts. Avoid clicking links and verify independently.';
      } else {
        riskLevel = 'Low Risk';
        explanation = 'No immediate red flags detected, but remain cautious with unknown contacts.';
      }
    } else {
      riskLevel = 'Low Risk';
      explanation = 'No specific risk identified based on the information provided. Stay vigilant.';
    }

    setResult({ riskLevel, explanation });
  };

  return (
    <div className="min-h-screen bg-gray-900">
      <NavBar />
      <div className="pt-20 pb-10 max-w-3xl mx-auto px-4">
        <h1 className="text-3xl font-semibold text-gray-100 mt-8 mb-6">Scam Checker</h1>
        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label className="block text-base text-gray-300 mb-2">How were you contacted?</label>
            <select
              name="contactMethod"
              value={formData.contactMethod}
              onChange={handleChange}
              className="w-full p-3 border border-gray-700 rounded-lg bg-gray-800 text-gray-300"
            >
              <option value="">Select</option>
              <option value="phone">Phone</option>
              <option value="email">Email</option>
              <option value="text">Text</option>
            </select>
          </div>
          <div>
            <label className="block text-base text-gray-300 mb-2">Who contacted you?</label>
            <select
              name="senderIdentity"
              value={formData.senderIdentity}
              onChange={handleChange}
              className="w-full p-3 border border-gray-700 rounded-lg bg-gray-800 text-gray-300"
            >
              <option value="">Select</option>
              <option value="friendOrFamily">Friend or Family</option>
              <option value="business">Business</option>
              <option value="unknown">Unknown</option>
            </select>
          </div>
          <div>
            <label className="block text-base text-gray-300 mb-2">Is there urgency?</label>
            <select
              name="urgency"
              value={formData.urgency}
              onChange={handleChange}
              className="w-full p-3 border border-gray-700 rounded-lg bg-gray-800 text-gray-300"
            >
              <option value="">Select</option>
              <option value="yes">Yes</option>
              <option value="no">No</option>
            </select>
          </div>
          <div>
            <label className="block text-base text-gray-300 mb-2">Are they asking for money?</label>
            <select
              name="paymentRequest"
              value={formData.paymentRequest}
              onChange={handleChange}
              className="w-full p-3 border border-gray-700 rounded-lg bg-gray-800 text-gray-300"
            >
              <option value="">Select</option>
              <option value="yes">Yes</option>
              <option value="no">No</option>
            </select>
          </div>
          {formData.senderIdentity === 'friendOrFamily' && (
            <div>
              <label className="block text-base text-gray-300 mb-2">
                Is this their usual number/contact method?
              </label>
              <select
                name="contactKnown"
                value={formData.contactKnown}
                onChange={handleChange}
                className="w-full p-3 border border-gray-700 rounded-lg bg-gray-800 text-gray-300"
              >
                <option value="">Select</option>
                <option value="yes">Yes</option>
                <option value="no">No</option>
              </select>
            </div>
          )}
          {(formData.paymentRequest === 'yes' || formData.urgency === 'yes') && (
            <div>
              <label className="block text-base text-gray-300 mb-2">
                Are they asking you to keep it secret?
              </label>
              <select
                name="secrecy"
                value={formData.secrecy}
                onChange={handleChange}
                className="w-full p-3 border border-gray-700 rounded-lg bg-gray-800 text-gray-300"
              >
                <option value="">Select</option>
                <option value="yes">Yes</option>
                <option value="no">No</option>
              </select>
            </div>
          )}
          <button
            type="submit"
            className="w-full p-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
          >
            Check Risk
          </button>
        </form>
        {result && (
          <div className="mt-6 p-6 bg-gray-800 rounded-lg shadow-sm border border-gray-700">
            <h2 className="text-xl font-semibold text-gray-100 mb-2">{result.riskLevel}</h2>
            <p className="text-base text-gray-300">{result.explanation}</p>
          </div>
        )}
      </div>
      <Footer />
    </div>
  );
}

export default ScamChecker;
